"use client";

import { useState } from "react";
import { Row, Col, Card, Form, Button, Alert, Spinner } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    const result = await login(formData.email, formData.password);
    result.success ? navigate("/employees") : setError(result.message);
    setLoading(false);
  };

  // Inline styles
  const pageStyle = {
    minHeight: "100vh",
    backgroundColor: "#6f42c1", // Purple
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
  };

  const logoWrapperStyle = {
    width: "100px",
    height: "100px",
    borderRadius: "50%",
    overflow: "hidden",
    margin: "0 auto",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.15)",
  };

  const logoImageStyle = {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    display: "block",
    border: "none",
  };

  return (
    <div style={pageStyle}>
      <Row className="justify-content-center w-100 m-0">
        <Col md={6} lg={5} xl={4}>
          <Card
            className="auth-card soft-card"
            style={{
              borderRadius: "12px",
              boxShadow: "0 0 20px rgba(0,0,0,0.2)",
            }}
          >
            <Card.Body className="p-5">
              {/* Logo */}
              <div className="text-center mb-4">
                <div style={logoWrapperStyle}>
                  <img
                    src="https://static.vecteezy.com/system/resources/thumbnails/005/425/371/small_2x/em-me-logo-design-template-graphic-branding-element-free-vector.jpg"
                    alt="Company Logo"
                    style={logoImageStyle}
                  />
                </div>
                <h2 className="auth-title mt-3 mb-1">Welcome Back</h2>
                <p className="text-muted">Sign in to your account</p>
              </div>

              {error && (
                <Alert variant="danger" className="mb-4">
                  <i className="fas fa-exclamation-circle me-2"></i>
                  {error}
                </Alert>
              )}

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>
                    <i className="fas fa-envelope me-2"></i>Email Address
                  </Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="form-control-soft"
                    placeholder="Enter your email"
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-4">
                  <Form.Label>
                    <i className="fas fa-lock me-2"></i>Password
                  </Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="form-control-soft"
                    placeholder="Enter your password"
                    required
                  />
                </Form.Group>

                <Button
                  type="submit"
                  className="btn-soft-primary w-100 mb-3"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <Spinner
                        as="span"
                        animation="border"
                        size="sm"
                        role="status"
                        className="me-2"
                      />
                      Signing In...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-sign-in-alt me-2"></i>Sign In
                    </>
                  )}
                </Button>

                <div className="text-center">
                  <span className="text-muted">Don't have an account? </span>
                  <Link to="/signup" className="auth-link">
                    Sign up here
                  </Link>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default LoginPage;
